    latitude: number;
    longitude: number;
  };
}

export interface Zone {
  id: number;
  warehouseId: number;
  name: string;
  description?: string;
  zoneType: 'standard' | 'refrigerated' | 'frozen' | 'hazardous' | 'secure';
  temperature?: {
    min: number;
    max: number;
    unit: 'C' | 'F';
  };
  aisles: Aisle[];
  capacity: Capacity;
}

export interface Aisle {
  id: number;
  zoneId: number;
  label: string;
  width: number;
  widthUnit: string;
