export interface Capacity {
  id: number;
  entityType: 'WAREHOUSE' | 'ZONE' | 'RACK' | 'SHELF';
  entityId: number;
  value: number;
  unit: string;
  description?: string;
  usedCapacity?: number;
  availableCapacity?: number;
  utilizationPercentage?: number;
}

export enum TILE_TYPES {
  AISLE = 'AISLE',
  RACK = 'RACK',
  DOCK = 'DOCK'
}

export type TileType = keyof typeof TILE_TYPES;

export interface WarehouseTile {
  type: TILE_TYPES;
  zone?: string;
  capacity?: {
    total: number;
    used: number;
  };
  status?: 'AVAILABLE' | 'OCCUPIED';
  truck?: {
    cargo: string;
    driver: string;
  };
  row: number;
  col: number;
  aisleId?: string;
}

export interface ZoneInfo {
  name: string;
