import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterLink } from '@angular/router';
import { Dock, DockAppointment } from './dock.model';
import { DockService } from './dock.service';
import { Heading1Component, Heading3Component, Heading4Component } from '../../ui-library/Typography/Typography.component';

@Component({
  selector: 'app-dock-detail',
  standalone: true,
  imports: [CommonModule, RouterLink, Heading1Component, Heading3Component, Heading4Component],
  template: `
    @if (dock) {
      <div class="space-y-6">
        <!-- Header -->
        <div class="flex items-center justify-between">
          <div class="flex items-center space-x-4">
            <button routerLink="/dock-management" class="p-2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300">
              <svg class="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div>
              <ui-heading1>{{ dock.name }}</ui-heading1>
              <p class="text-gray-600 dark:text-gray-400 capitalize">{{ dock.type }} dock</p>
            </div>
            <span [class]="getStatusBadgeClass(dock.status)" 
                  class="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium">
              {{ dock.status | titlecase }}
            </span>
          </div>
          <button class="btn btn-primary">
            <svg class="h-5 w-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Schedule Appointment
          </button>
        </div>

        <!-- Current Status Card -->
        <div class="card p-6">
          <ui-heading3 class="mb-4">Current Status</ui-heading3>

          @if (dock.status === 'available') {
            <div class="p-4 bg-success-50 dark:bg-success-900/20 rounded-lg border border-success-200 dark:border-success-800">
              <div class="flex items-center">
                <svg class="h-8 w-8 text-success-600 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <div>
                  <ui-heading4 class="text-success-800 dark:text-success-200">Available</ui-heading4>
                  <p class="text-success-700 dark:text-success-300">This dock is ready for scheduling</p>
                </div>
              </div>
            </div>
          }

          @if (dock.status === 'occupied' && dock.currentTruck) {
            <div class="p-4 bg-primary-50 dark:bg-primary-900/20 rounded-lg border border-primary-200 dark:border-primary-800">
              <div class="flex items-start">
                <svg class="h-8 w-8 text-primary-600 mr-3 mt-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 7V3a2 2 0 012-2h4a2 2 0 012 2v4m0 0V7a2 2 0 012 2v9a2 2 0 01-2 2H6a2 2 0 01-2-2v-9a2 2 0 012-2h0V7" />
                </svg>
                <div class="flex-1">
                  <ui-heading4 class="text-primary-800 dark:text-primary-200 mb-3">Currently Occupied</ui-heading4>
                  <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <p class="text-sm font-medium text-primary-700 dark:text-primary-300">Truck Information</p>
                      <div class="mt-1 space-y-1">
                        <p class="text-sm text-primary-600 dark:text-primary-400">License: {{ dock.currentTruck.licensePlate }}</p>
                        <p class="text-sm text-primary-600 dark:text-primary-400">Type: {{ dock.currentTruck.truckType }}</p>
                        <p class="text-sm text-primary-600 dark:text-primary-400">Capacity: {{ dock.currentTruck.capacity }} {{ dock.currentTruck.capacityUnit }}</p>
                      </div>
                    </div>
                    <div>
                      <p class="text-sm font-medium text-primary-700 dark:text-primary-300">Driver & Carrier</p>
                      <div class="mt-1 space-y-1">
                        <p class="text-sm text-primary-600 dark:text-primary-400">Driver: {{ dock.currentTruck.driverName }}</p>
                        <p class="text-sm text-primary-600 dark:text-primary-400">Phone: {{ dock.currentTruck.driverPhone }}</p>
                        <p class="text-sm text-primary-600 dark:text-primary-400">Carrier: {{ dock.currentTruck.carrierName }}</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          }

          @if (dock.status === 'maintenance') {
            <div class="p-4 bg-warning-50 dark:bg-warning-900/20 rounded-lg border border-warning-200 dark:border-warning-800">
              <div class="flex items-center">
                <svg class="h-8 w-8 text-warning-600 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <div>
                  <ui-heading4 class="text-warning-800 dark:text-warning-200">Under Maintenance</ui-heading4>
                  <p class="text-warning-700 dark:text-warning-300">This dock is temporarily unavailable for operations</p>
                </div>
              </div>
            </div>
          }

          @if (dock.status === 'reserved') {
            <div class="p-4 bg-secondary-50 dark:bg-secondary-900/20 rounded-lg border border-secondary-200 dark:border-secondary-800">
              <div class="flex items-center">
                <svg class="h-8 w-8 text-secondary-600 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                </svg>
                <div>
                  <ui-heading4 class="text-secondary-800 dark:text-secondary-200">Reserved</ui-heading4>
                  <p class="text-secondary-700 dark:text-secondary-300">This dock is reserved for upcoming appointments</p>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    } @else {
      <div class="flex items-center justify-center h-64">
        <div class="text-center">
          <svg class="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
          <ui-heading4 class="mt-2">Dock not found</ui-heading4>
          <p class="mt-1 text-sm text-gray-500 dark:text-gray-400">The requested dock could not be found.</p>
        </div>
      </div>
    }
  `
})
export class DockDetailComponent implements OnInit {
  dock: Dock | null = null;
  dockId: number = 0;

  private route = inject(ActivatedRoute);
  private dockService = inject(DockService);

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.dockId = parseInt(params['id']);
      this.loadDockDetails();
    });
  }

  loadDockDetails(): void {
    this.dockService.getDock(this.dockId).subscribe(dock => {
      this.dock = dock || null;
    });
  }

  getStatusBadgeClass(status: string): string {
    switch (status) {
      case 'available': return 'bg-success-100 text-success-800';
      case 'occupied': return 'bg-primary-100 text-primary-800';
      case 'maintenance': return 'bg-warning-100 text-warning-800';
      case 'reserved': return 'bg-secondary-100 text-secondary-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  }
}

import { Component, input, output } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { Dock } from './dock.model';
import { LucideAngularModule, CheckCircle, Package, AlertTriangle, Lock } from 'lucide-angular';
import { Heading3Component } from '../../ui-library/Typography/Typography.component';

@Component({
  selector: 'app-dock-listing',
  standalone: true,
  imports: [CommonModule, RouterLink, LucideAngularModule, Heading3Component],
  template: `
    <!-- Dock Grid -->
    <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      @for (dock of docks(); track dock.id) {
        <div class="card p-6 transition-all hover:shadow-md cursor-pointer"
            [class]="getDockCardClass(dock)"
            [routerLink]="['/dock-management', dock.id]">

          <!-- Dock Header -->
          <div class="flex items-center justify-between mb-4">
            <div class="flex items-center">
              <div [class]="getDockIconClass(dock.status)" class="p-2 rounded-lg mr-3">
                <lucide-icon [img]="getDockIcon(dock.status)" size="24" class="text-current"></lucide-icon>
              </div>
              <div>
                <ui-heading3>{{ dock.name }}</ui-heading3>
                <p class="text-sm text-gray-500 dark:text-gray-400 capitalize">{{ dock.type }} dock</p>
              </div>
            </div>
            <span [class]="getStatusBadgeClass(dock.status)" 
                  class="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium">
              {{ dock.status | titlecase }}
            </span>
          </div>

          <!-- Current Status -->
          <div class="space-y-3">
            @if (dock.status === 'available') {
              <div class="p-3 bg-success-50 dark:bg-success-900/20 rounded-lg border border-success-200 dark:border-success-800">
                <div class="flex items-center">
                  <lucide-icon [img]="CheckCircleIcon" size="20" class="text-success-600 mr-2"></lucide-icon>
                  <span class="text-sm font-medium text-success-800 dark:text-success-200">Available for scheduling</span>
                </div>
              </div>
            }

            @if (dock.status === 'occupied' && dock.currentTruck) {
              <div class="p-3 bg-primary-50 dark:bg-primary-900/20 rounded-lg border border-primary-200 dark:border-primary-800">
                <div class="flex items-center justify-between mb-2">
                  <span class="text-sm font-medium text-primary-800 dark:text-primary-200">Currently Occupied</span>
                </div>
                <div class="space-y-1">
                  <div class="text-sm text-primary-700 dark:text-primary-300">
                    <strong>Truck:</strong> {{ dock.currentTruck.licensePlate }}
                  </div>
                  <div class="text-sm text-primary-700 dark:text-primary-300">
                    <strong>Carrier:</strong> {{ dock.currentTruck.carrierName }}
                  </div>
                  <div class="text-sm text-primary-700 dark:text-primary-300">
                    <strong>Driver:</strong> {{ dock.currentTruck.driverName }}
                  </div>
                </div>
              </div>
            }

            @if (dock.status === 'maintenance') {
              <div class="p-3 bg-warning-50 dark:bg-warning-900/20 rounded-lg border border-warning-200 dark:border-warning-800">
                <div class="flex items-center">
                  <lucide-icon [img]="AlertTriangleIcon" size="20" class="text-warning-600 mr-2"></lucide-icon>
                  <span class="text-sm font-medium text-warning-800 dark:text-warning-200">Under maintenance</span>
                </div>
              </div>
            }

            @if (dock.status === 'reserved') {
              <div class="p-3 bg-secondary-50 dark:bg-secondary-900/20 rounded-lg border border-secondary-200 dark:border-secondary-800">
                <div class="flex items-center">
                  <lucide-icon [img]="LockIcon" size="20" class="text-secondary-600 mr-2"></lucide-icon>
                  <span class="text-sm font-medium text-secondary-800 dark:text-secondary-200">Reserved</span>
                </div>
              </div>
            }
          </div>
        </div>
      }
    </div>
  `
})
export class DockListingComponent {
  // Signal-based input
  docks = input<Dock[]>([]);

  // Outputs
  dockSelected = output<Dock>();

  // Lucide icons
  CheckCircleIcon = CheckCircle;
  PackageIcon = Package;
  AlertTriangleIcon = AlertTriangle;
  LockIcon = Lock;

  getDockIcon(status: string) {
    switch (status) {
      case 'available': return this.CheckCircleIcon;
      case 'occupied': return this.PackageIcon;
      case 'maintenance': return this.AlertTriangleIcon;
      case 'reserved': return this.LockIcon;
      default: return this.PackageIcon;
    }
  }

  getDockCardClass(dock: Dock): string {
    const baseClass = 'border-l-2';
    switch (dock.status) {
      case 'available': return `${baseClass} border-l-success-500`;
      case 'occupied': return `${baseClass} border-l-primary-500`;
      case 'maintenance': return `${baseClass} border-l-warning-500`;
      case 'reserved': return `${baseClass} border-l-secondary-500`;
      default: return `${baseClass} border-l-gray-500`;
    }
  }

  getDockIconClass(status: string): string {
    switch (status) {
      case 'available': return 'bg-success-100 text-success-600';
      case 'occupied': return 'bg-primary-100 text-primary-600';
      case 'maintenance': return 'bg-warning-100 text-warning-600';
      case 'reserved': return 'bg-secondary-100 text-secondary-600';
      default: return 'bg-gray-100 text-gray-600';
    }
  }

  getStatusBadgeClass(status: string): string {
    switch (status) {
      case 'available': return 'bg-success-100 text-success-800';
      case 'occupied': return 'bg-primary-100 text-primary-800';
      case 'maintenance': return 'bg-warning-100 text-warning-800';
      case 'reserved': return 'bg-secondary-100 text-secondary-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  }
}

import { Component, OnInit, inject } from '@angular/core';

import { LucideAngularModule, Plus } from 'lucide-angular';

import { Dock } from './dock.model';
import { DockStatsComponent } from './dock-stats.component';
import { DockListingComponent } from './dock-listing.component';
import { DockFormScheduleAppointmentComponent } from './dock-form-schedule-appointment.component';
import { DockService } from './dock.service';
import { Heading1Component, SubtitleComponent } from '../../ui-library/Typography/Typography.component';

@Component({
  selector: 'app-dock-management',
  standalone: true,
  imports: [
    LucideAngularModule,
    DockStatsComponent,
    DockListingComponent,
    DockFormScheduleAppointmentComponent,
    Heading1Component,
    SubtitleComponent
],
  template: `
    <div class="space-y-6">
      <!-- Header -->
      <div class="flex justify-between items-center pb-4">
        <div>
          <ui-heading1>Dock Management</ui-heading1>
          <ui-subtitle>Schedule and manage dock operations</ui-subtitle>
        </div>
        <button (click)="showScheduleModal = true" class="btn btn-primary">
          <lucide-icon [img]="PlusIcon" size="18" class="mr-2"></lucide-icon>
          Schedule Appointment
        </button>
      </div>

      <!-- Stats Component -->
      <app-dock-stats [docks]="docks" />

      <!-- Listing Component -->
      <app-dock-listing [docks]="docks" class="inline-block mt-2" />

      <!-- Schedule Appointment Modal -->
      <app-dock-form-schedule-appointment
        [showModal]="showScheduleModal"
        [availableDocks]="getAvailableDocks()"
        (modalClosed)="showScheduleModal = false"
        (appointmentScheduled)="onAppointmentScheduled($event)" />
    </div>
  `
})
export class DockManagementComponent implements OnInit {
  docks: Dock[] = [];
  showScheduleModal = false;

  // Lucide icons
  PlusIcon = Plus;

  private dockService = inject(DockService);

  ngOnInit(): void {
    this.loadDockData();
  }

  loadDockData(): void {
    this.dockService.getDocks().subscribe(docks => {
      this.docks = docks;
    });
  }

  getAvailableDocks(): Dock[] {
    return this.docks.filter(dock => dock.status === 'available' || dock.status === 'reserved');
  }

  onAppointmentScheduled(appointmentData: any): void {
    console.log('New appointment scheduled:', appointmentData);
    // In a real app, you would update the dock status and refresh the data
    this.loadDockData();
  }
}

import { Component, input } from '@angular/core';

import { Dock } from './dock.model';
import { LucideAngularModule, CheckCircle, Package, AlertTriangle, Calendar } from 'lucide-angular';

@Component({
  selector: 'app-dock-stats',
  standalone: true,
  imports: [LucideAngularModule],
  template: `
    <!-- Dock Overview -->
    <div class="grid grid-cols-1 md:grid-cols-4 gap-6">
      <div class="card p-6">
        <div class="flex items-center">
          <div class="p-2 bg-success-100 rounded-lg">
            <lucide-icon [img]="CheckCircleIcon" size="24" class="text-success-600"></lucide-icon>
          </div>
          <div class="ml-4">
            <p class="text-sm font-medium text-gray-600 dark:text-gray-400">Available Docks</p>
            <p class="text-2xl font-semibold text-gray-900 dark:text-white">{{ getAvailableDocks() }}</p>
          </div>
        </div>
      </div>

      <div class="card p-6">
        <div class="flex items-center">
          <div class="p-2 bg-primary-100 rounded-lg">
            <lucide-icon [img]="PackageIcon" size="24" class="text-primary-600"></lucide-icon>
          </div>
          <div class="ml-4">
            <p class="text-sm font-medium text-gray-600 dark:text-gray-400">Occupied Docks</p>
            <p class="text-2xl font-semibold text-gray-900 dark:text-white">{{ getOccupiedDocks() }}</p>
          </div>
        </div>
      </div>

      <div class="card p-6">
        <div class="flex items-center">
          <div class="p-2 bg-warning-100 rounded-lg">
            <lucide-icon [img]="AlertTriangleIcon" size="24" class="text-warning-600"></lucide-icon>
          </div>
          <div class="ml-4">
            <p class="text-sm font-medium text-gray-600 dark:text-gray-400">Under Maintenance</p>
            <p class="text-2xl font-semibold text-gray-900 dark:text-white">{{ getMaintenanceDocks() }}</p>
          </div>
        </div>
      </div>

      <div class="card p-6">
        <div class="flex items-center">
          <div class="p-2 bg-secondary-100 rounded-lg">
            <lucide-icon [img]="CalendarIcon" size="24" class="text-secondary-600"></lucide-icon>
          </div>
          <div class="ml-4">
            <p class="text-sm font-medium text-gray-600 dark:text-gray-400">Today's Appointments</p>
            <p class="text-2xl font-semibold text-gray-900 dark:text-white">{{ getTodayAppointments() }}</p>
          </div>
        </div>
      </div>
    </div>
  `
})
export class DockStatsComponent {
  // Signal-based input
  docks = input<Dock[]>([]);

  // Lucide icons
  CheckCircleIcon = CheckCircle;
  PackageIcon = Package;
  AlertTriangleIcon = AlertTriangle;
  CalendarIcon = Calendar;

  getAvailableDocks(): number {
    return this.docks().filter(dock => dock.status === 'available').length;
  }

  getOccupiedDocks(): number {
    return this.docks().filter(dock => dock.status === 'occupied').length;
  }

  getMaintenanceDocks(): number {
    return this.docks().filter(dock => dock.status === 'maintenance').length;
  }

  getTodayAppointments(): number {
    // Mock data - in real app would calculate from actual appointments
    return 5;
  }
}

import { Component, input, output } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'ui-dropdown',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="w-full">
      @if (label()) {
        <label class="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">{{ label() }}</label>
      }
      <select 
        [value]="value()" 
        (change)="onSelectionChange($event)"
        class="w-full px-3 py-2 h-10 text-sm border border-gray-300 dark:border-gray-600 rounded-md shadow-sm bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 transition-colors duration-200 cursor-pointer hover:border-gray-400 dark:hover:border-gray-500">
        @for (option of options(); track option.value) {
          <option [value]="option.value" class="py-2">
            {{ option.label }}
          </option>
        }
      </select>
    </div>
  `
})
export class DropdownComponent {
  options = input<{ value: string; label: string }[]>([]);
  value = input<string>('');
  label = input<string>('');
  valueChange = output<string>();

  onSelectionChange(event: Event) {
    const target = event.target as HTMLSelectElement;
    this.valueChange.emit(target.value);
  }
}

import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, RouterLink } from '@angular/router';
import { EmployeesService } from './employees.service';
import { Employee } from './employees.interfaces';
import { LucideAngularModule, ArrowLeft, User, Mail, Phone, Calendar, MapPin, Shield, Building, Clock, Edit } from 'lucide-angular';
import { Heading1Component, Heading3Component, Heading4Component } from '../../ui-library/Typography/Typography.component';

@Component({
  selector: 'app-employee-detail',
  standalone: true,
  imports: [CommonModule, RouterLink, LucideAngularModule, Heading1Component, Heading3Component, Heading4Component],
  template: `
    @if (employee) {
      <div class="space-y-6">
        <!-- Header -->
        <div class="flex items-center justify-between">
          <div class="flex items-center space-x-4">
            <button routerLink="/employees" class="p-2 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300">
              <lucide-icon [img]="ArrowLeftIcon" size="20"></lucide-icon>
            </button>
            <div>
              <ui-heading1>{{ employee.fullName }}</ui-heading1>
              <p class="text-gray-600 dark:text-gray-400">Employee Details</p>
            </div>
            <span [class]="getStatusClass(employee.isActive)"
                  class="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium">
              {{ employee.isActive ? 'Active' : 'Inactive' }}
            </span>
          </div>
          <div class="flex space-x-3">
            <button class="btn btn-secondary">
              <lucide-icon [img]="EditIcon" size="18" class="mr-2"></lucide-icon>
              Edit Employee
            </button>
          </div>
        </div>

        <!-- Employee Information Cards -->
        <div class="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <!-- Basic Information -->
          <div class="card p-6">
            <ui-heading3 class="mb-4">
              <lucide-icon [img]="UserIcon" size="20"></lucide-icon>
              Personal Information
            </ui-heading3>
            <div class="space-y-4">
              <div class="flex items-center">
                <div class="flex-shrink-0 h-16 w-16">
                  <div class="h-16 w-16 rounded-full bg-primary-100 flex items-center justify-center">
                    <span class="text-xl font-medium text-primary-600">{{ employee.fullName.charAt(0) }}</span>
                  </div>
                </div>
                <div class="ml-4">
                  <ui-heading4>{{ employee.fullName }}</ui-heading4>
                  <p class="text-sm text-gray-500 dark:text-gray-400">Employee ID: #{{ employee.id }}</p>
                </div>
              </div>

              <div>
                <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Email Address</label>
                <div class="flex items-center mt-1">
                  <lucide-icon [img]="MailIcon" size="16" class="mr-2 text-gray-400"></lucide-icon>
                  <a href="mailto:{{ employee.email }}"
                     class="text-sm text-primary-600 hover:text-primary-500">{{ employee.email }}</a>
                </div>
              </div>

              @if (employee.phone) {
                <div>
                  <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Phone Number</label>
                  <div class="flex items-center mt-1">
                    <lucide-icon [img]="PhoneIcon" size="16" class="mr-2 text-gray-400"></lucide-icon>
                    <p class="text-sm text-gray-900 dark:text-white">{{ employee.phone }}</p>
                  </div>
                </div>
              }
            </div>
          </div>

          <!-- Employment Information -->
          <div class="card p-6">
            <ui-heading3 class="mb-4">
              <lucide-icon [img]="CalendarIcon" size="20"></lucide-icon>
              Employment Information
            </ui-heading3>
            <div class="space-y-4">
              <div>
                <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Hire Date</label>
                <p class="text-sm text-gray-900 dark:text-white mt-1">{{ employee.hireDate | date:'MMM d, y' }}</p>
              </div>

              <div>
                <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Employment Status</label>
                <span [class]="getStatusClass(employee.isActive)"
                      class="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium mt-1">
                  {{ employee.isActive ? 'Active' : 'Inactive' }}
                </span>
              </div>

              @if (employee.lastLogin) {
                <div>
                  <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Last Login</label>
                  <div class="flex items-center mt-1">
                    <lucide-icon [img]="ClockIcon" size="16" class="mr-2 text-gray-400"></lucide-icon>
                    <p class="text-sm text-gray-900 dark:text-white">{{ employee.lastLogin | date:'MMM d, y h:mm a' }}</p>
                  </div>
                </div>
              }
            </div>
          </div>
        </div>

        <!-- Roles and Permissions -->
        <div class="card p-6">
          <ui-heading3 class="mb-4">
            <lucide-icon [img]="ShieldIcon" size="20"></lucide-icon>
            Roles and Permissions
          </ui-heading3>
          <div class="space-y-4">
            @for (role of employee.roles; track role.roleName) {
              <div class="p-4 bg-gray-50 dark:bg-dark-700 rounded-lg">
                <div class="flex items-center justify-between">
                  <div>
                    <ui-heading4>{{ role.roleName }}</ui-heading4>
                    <p class="text-sm text-gray-500 dark:text-gray-400">
                      Assigned on {{ role.assignedDate | date:'MMM d, y' }}
                      @if (role.assignedByName) {
                        <span> by {{ role.assignedByName }}</span>
                      }
                    </p>
                  </div>
                  <span class="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-primary-100 text-primary-800">
                    Active
                  </span>
                </div>
              </div>
            } @empty {
              <div class="text-center py-8 text-gray-500 dark:text-gray-400">
                <lucide-icon [img]="ShieldIcon" size="48" class="mx-auto mb-4 text-gray-400"></lucide-icon>
                <p class="text-sm">No roles assigned to this employee</p>
              </div>
            }
          </div>
        </div>

        <!-- Warehouse Assignments -->
        <div class="card p-6">
          <ui-heading3 class="mb-4">
            <lucide-icon [img]="BuildingIcon" size="20"></lucide-icon>
            Warehouse Assignments
          </ui-heading3>
          <div class="space-y-4">
            @for (assignment of employee.warehouseAssignments; track assignment.warehouseName) {
              <div class="p-4 bg-gray-50 dark:bg-dark-700 rounded-lg">
                <div class="flex items-center justify-between">
                  <div>
                    <ui-heading4>{{ assignment.warehouseName }}</ui-heading4>
                    <p class="text-sm text-gray-500 dark:text-gray-400">
                      Assigned from {{ assignment.assignedFrom | date:'MMM d, y' }}
                      @if (assignment.assignedUntil) {
                        <span> until {{ assignment.assignedUntil | date:'MMM d, y' }}</span>
                      }
                    </p>
                  </div>
                  <span [class]="assignment.isActive ? 'bg-success-100 text-success-800' : 'bg-gray-100 text-gray-800'"
                        class="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium">
                    {{ assignment.isActive ? 'Active' : 'Inactive' }}
                  </span>
                </div>
              </div>
            } @empty {
              <div class="text-center py-8 text-gray-500 dark:text-gray-400">
                <lucide-icon [img]="BuildingIcon" size="48" class="mx-auto mb-4 text-gray-400"></lucide-icon>
                <p class="text-sm">No warehouse assignments for this employee</p>
              </div>
            }
          </div>
        </div>

        <!-- Account Details -->
        <div class="card p-6">
          <ui-heading3 class="mb-4">
            <lucide-icon [img]="ClockIcon" size="20"></lucide-icon>
            Account Details
          </ui-heading3>
          <div class="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Account Created</label>
              <p class="text-sm text-gray-900 dark:text-white mt-1">{{ employee.createdAt | date:'MMM d, y h:mm a' }}</p>
            </div>
            <div>
              <label class="text-sm font-medium text-gray-700 dark:text-gray-300">Last Updated</label>
              <p class="text-sm text-gray-900 dark:text-white mt-1">{{ employee.updatedAt | date:'MMM d, y h:mm a' }}</p>
            </div>
          </div>
        </div>
      </div>
    } @else {
      <div class="flex items-center justify-center h-64">
        <div class="text-center">
          <lucide-icon [img]="UserIcon" size="48" class="mx-auto text-gray-400 mb-4"></lucide-icon>
          <ui-heading4 class="mt-2">Employee not found</ui-heading4>
          <p class="mt-1 text-sm text-gray-500 dark:text-gray-400">The requested employee could not be found.</p>
        </div>
      </div>
    }
  `
})
export class EmployeeDetailComponent implements OnInit {
  employee: Employee | null = null;
  employeeId: number = 0;

  // Lucide icons
  ArrowLeftIcon = ArrowLeft;
  UserIcon = User;
  MailIcon = Mail;
  PhoneIcon = Phone;
  CalendarIcon = Calendar;
  MapPinIcon = MapPin;
  ShieldIcon = Shield;
  BuildingIcon = Building;
  ClockIcon = Clock;
  EditIcon = Edit;

  private route = inject(ActivatedRoute);
  private employeesService = inject(EmployeesService);

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.employeeId = parseInt(params['id']);
      this.loadEmployeeDetails();
    });
  }

  loadEmployeeDetails(): void {
    this.employeesService.getEmployee(this.employeeId).subscribe(employee => {
      this.employee = employee || null;
    });
  }

  getStatusClass(isActive: boolean): string {
    return isActive ? 'bg-success-100 text-success-800' : 'bg-gray-100 text-gray-800';
  }
}

